// frontend/src/pages/Products.jsx
import React, { useState, useEffect } from 'react';
import ProductGrid from '../components/products/ProductGrid';
import ProductFilter from '../components/products/ProductFilter';
import { getProducts } from '../services/api';
import './Products.css';

/**
 * Página de Productos - Muestra el catálogo de productos
 * Incluye filtros y vista en grid responsive
 */
const Products = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [filters, setFilters] = useState({
    category: '',
    producer: '',
    location: '',
    priceMin: '',
    priceMax: '',
    search: ''
  });
  const [isLoading, setIsLoading] = useState(true);

  // Cargar productos al montar el componente
  useEffect(() => {
    loadProducts();
  }, []);

  // Aplicar filtros cuando cambien
  useEffect(() => {
    applyFilters();
  }, [products, filters]);

  /**
   * Cargar productos desde la API
   */
  const loadProducts = async () => {
    try {
      setIsLoading(true);
      const data = await getProducts();
      setProducts(data);
    } catch (error) {
      console.error('Error al cargar productos:', error);
    } finally {
      setIsLoading(false);
    }
  };

  /**
   * Aplicar filtros a los productos
   */
  const applyFilters = () => {
    let filtered = [...products];

    // Filtro por búsqueda
    if (filters.search) {
      filtered = filtered.filter(product =>
        product.name.toLowerCase().includes(filters.search.toLowerCase()) ||
        product.description.toLowerCase().includes(filters.search.toLowerCase())
      );
    }

    // Filtro por categoría
    if (filters.category) {
      filtered = filtered.filter(product => product.category === filters.category);
    }

    // Filtro por productor
    if (filters.producer) {
      filtered = filtered.filter(product => product.producer === filters.producer);
    }

    // Filtro por ubicación
    if (filters.location) {
      filtered = filtered.filter(product => product.location === filters.location);
    }

    // Filtro por precio mínimo
    if (filters.priceMin) {
      filtered = filtered.filter(product => product.price >= parseFloat(filters.priceMin));
    }

    // Filtro por precio máximo
    if (filters.priceMax) {
      filtered = filtered.filter(product => product.price <= parseFloat(filters.priceMax));
    }

    setFilteredProducts(filtered);
  };

  /**
   * Manejar cambios en los filtros
   * @param {Object} newFilters - Nuevos filtros aplicados
   */
  const handleFilterChange = (newFilters) => {
    setFilters(prevFilters => ({
      ...prevFilters,
      ...newFilters
    }));
  };

  /**
   * Limpiar todos los filtros
   */
  const clearFilters = () => {
    setFilters({
      category: '',
      producer: '',
      location: '',
      priceMin: '',
      priceMax: '',
      search: ''
    });
  };

  return (
    <div className="products-page">
      <div className="container">
        <div className="products-header">
          <h1>Nuestros Productos</h1>
          <p>Descubre la mejor selección de productos rurales frescos y de calidad</p>
        </div>

        <div className="products-content">
          {/* Sidebar con filtros */}
          <aside className="products-sidebar">
            <ProductFilter
              filters={filters}
              onFilterChange={handleFilterChange}
              onClearFilters={clearFilters}
              products={products}
            />
          </aside>

          {/* Grid de productos */}
          <main className="products-main">
            {isLoading ? (
              <div className="loading">
                <i className="fas fa-spinner fa-spin"></i>
                <p>Cargando productos...</p>
              </div>
            ) : (
              <>
                <div className="products-results">
                  <p>{filteredProducts.length} productos encontrados</p>
                </div>
                <ProductGrid products={filteredProducts} />
              </>
            )}
          </main>
        </div>
      </div>
    </div>
  );
};

export default Products;
