// frontend/src/components/cart/Cart.jsx
import React from 'react';
import { useCart } from '../../context/CartContext';
import './Cart.css';

const Cart = () => {
  const { 
    cartItems, 
    removeFromCart, 
    updateQuantity, 
    clearCart, 
    getCartTotal,
    getCartItemsCount 
  } = useCart();

  if (cartItems.length === 0) {
    return (
      <div className="cart-empty">
        <h2>Tu carrito está vacío</h2>
        <p>Agrega algunos productos para comenzar</p>
      </div>
    );
  }

  return (
    <div className="cart">
      <div className="cart-header">
        <h2>Carrito de Compras</h2>
        <span className="cart-count">({getCartItemsCount()} productos)</span>
      </div>

      <div className="cart-items">
        {cartItems.map((item) => (
          <div key={item._id} className="cart-item">
            <div className="item-image">
              <img 
                src={item.images?.[0]?.url || '/images/placeholder.jpg'} 
                alt={item.name}
                onError={(e) => {
                  e.target.src = '/images/placeholder.jpg';
                }}
              />
            </div>
            
            <div className="item-details">
              <h3>{item.name}</h3>
              <p className="item-producer">{item.producer?.name}</p>
              <p className="item-price">${item.price.toFixed(2)} por {item.unit}</p>
            </div>

            <div className="item-quantity">
              <button 
                onClick={() => updateQuantity(item._id, item.quantity - 1)}
                className="quantity-btn"
              >
                -
              </button>
              <span className="quantity">{item.quantity}</span>
              <button 
                onClick={() => updateQuantity(item._id, item.quantity + 1)}
                className="quantity-btn"
              >
                +
              </button>
            </div>

            <div className="item-total">
              <p>${(item.price * item.quantity).toFixed(2)}</p>
            </div>

            <button 
              onClick={() => removeFromCart(item._id)}
              className="remove-btn"
              title="Eliminar producto"
            >
              <i className="fas fa-trash"></i>
            </button>
          </div>
        ))}
      </div>

      <div className="cart-footer">
        <div className="cart-total">
          <h3>Total: ${getCartTotal().toFixed(2)}</h3>
        </div>
        
        <div className="cart-actions">
          <button 
            onClick={clearCart}
            className="btn btn-secondary"
          >
            Limpiar Carrito
          </button>
          <button 
            className="btn btn-primary"
            onClick={() => {
              // Aquí iría la lógica para proceder al checkout
              alert('Funcionalidad de checkout pendiente');
            }}
          >
            Proceder al Pago
          </button>
        </div>
      </div>
    </div>
  );
};

export default Cart;
