// frontend/src/components/admin/ProductManager.jsx - Agregar keys
import React, { useState, useEffect } from 'react';

const ProductManager = () => {
  const [products, setProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    try {
      setIsLoading(true);
      const mockProducts = [
        { id: 1, name: 'Producto Demo', price: 10.99, category: 'Frutas' },
        { id: 2, name: 'Producto Demo 2', price: 15.99, category: 'Verduras' }
      ];
      setProducts(mockProducts);
    } catch (error) {
      console.error('Error al cargar productos:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="product-manager">
      <div className="manager-header">
        <h2>Gestión de Productos</h2>
        <button className="btn btn-primary">
          <i className="fas fa-plus"></i>
          Agregar Producto
        </button>
      </div>

      {isLoading ? (
        <div className="loading">
          <i className="fas fa-spinner fa-spin"></i>
          <p>Cargando productos...</p>
        </div>
      ) : (
        <div className="products-table">
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Nombre</th>
                <th>Precio</th>
                <th>Categoría</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {products.map(product => (
                <tr key={product.id || `product-row-${product.name}`}>
                  <td>{product.id}</td>
                  <td>{product.name}</td>
                  <td>${product.price}</td>
                  <td>{product.category}</td>
                  <td>
                    <button className="btn btn-secondary btn-sm">
                      <i className="fas fa-edit"></i>
                    </button>
                    <button className="btn btn-danger btn-sm">
                      <i className="fas fa-trash"></i>
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default ProductManager;
