// frontend/src/components/products/ProductCard.jsx - Actualizar para manejar estructura del backend
import React from 'react';
import { useCart } from '../../context/CartContext';

const ProductCard = ({ product }) => {
  const { addToCart } = useCart();

  const handleAddToCart = () => {
    addToCart(product);
  };

  // Manejar diferentes estructuras de datos
  const productName = product.name || product.nombreproducto || 'Producto sin nombre';
  const productDescription = product.description || product.descripcion || 'Sin descripción';
  const productPrice = product.price || product.precio || 0;
  const producerName = product.producer?.name || product.producer?.nombreproductor || product.nombreproductor || 'Productor desconocido';
  const categoryName = product.category?.name || product.category?.nombrecategoria || product.nombrecategoria || 'Sin categoría';
  const locationName = product.producer?.location?.city || product.ciudad || 'Ubicación desconocida';
  const productImage = product.images?.[0]?.url || product.image || '/images/placeholder.jpg';

  return (
    <div className="product-card">
      <div className="product-image">
        <img 
          src={productImage} 
          alt={productName}
          onError={(e) => {
            e.target.src = '/images/placeholder.jpg';
          }}
        />
      </div>
      
      <div className="product-info">
        <h3 className="product-name">{productName}</h3>
        <p className="product-description">{productDescription}</p>
        
        <div className="product-details">
          <p className="product-producer">
            <i className="fas fa-user"></i>
            {producerName}
          </p>
          <p className="product-category">
            <i className="fas fa-tag"></i>
            {categoryName}
          </p>
          <p className="product-location">
            <i className="fas fa-map-marker-alt"></i>
            {locationName}
          </p>
        </div>
        
        <div className="product-footer">
          <span className="product-price">${Number(productPrice).toFixed(2)}</span>
          <button 
            className="btn btn-primary add-to-cart-btn"
            onClick={handleAddToCart}
          >
            <i className="fas fa-cart-plus"></i>
            Agregar
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
