      isAvailable: true
    }
  ];
};

const getMockCategories = () => {
  return [
    { _id: '1', name: 'Frutas' },
    { _id: '2', name: 'Verduras' },
    { _id: '3', name: 'Lácteos' }
  ];
};

const getMockProducers = () => {
  return [
    { _id: '1', name: 'Finca Los Manzanos' },
    { _id: '2', name: 'Lácteos San José' }
