const getMockLocations = () => {
  return [
    { _id: '1', city: 'Boyacá', department: 'Boyacá' },
    { _id: '2', city: 'Bogotá', department: 'Cundinamarca' }
  ];
};

const getFilteredMockProducts = (filters) => {
  return getMockProducts();
};
